mapper.select:
select error_code errorCode, error_message errorMessage, type, business_code businessCode, sort_number sortNumber, status, remarks, create_time createTime from busi_sys_error_code
status = #{status}
and type = #{type}
and error_code = #{errorCode}
and error_message like #{errorMessage}
and business_code = #{businessCode}
ORDER BY type, business_code, sort_number, error_code
